import React, { useState } from 'react';
import './App.css';
import Stars from './Stars'; // Import Stars component

function App() {
  const [movieRating, setMovieRating] = useState(0);

  const handleRate = (rating) => {
    setMovieRating(rating);
  };

  return (
    <div className="App">
      <h1>My Movie Voting and Review App</h1>
      <div>
        <h2>Movie Title</h2>
        <p>Synopsis: Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
        <Stars totalStars={5} initialRating={movieRating} onRate={handleRate} />
        <p>Your rating: {movieRating}</p>
      </div>
    </div>
  );
}

export default App;
