import React from 'react';
import './App.css';
import Stars from './Stars'; // Import Stars component
import MovieList from './MovieList';

const movies = [
   {
       id: 1,
       title: 'Inception',
       image: 'https://example.com/inception.jpg',
       synopsis: 'A movie about dreams within dreams.',
       reviews: ['Great movie!', 'Mind-blowing plot.']
   },
   {
       id: 2,
       title: 'The Dark Knight',
       image: 'https://example.com/dark_knight.jpg',
       synopsis: 'Batman battles the Joker.',
       reviews: ['Heath Ledger was amazing!']
   }
];

function App() {
   const handleReviewSubmit = (movieId, review) => {
       // Handle review submission (mock example, in a real app, update state or send to server)
       console.log(`Adding review for movie ${movieId}: ${review}`);
   };

   const [movieRating, setMovieRating] = useState(0);

  const handleRate = (rating) => {
    setMovieRating(rating);
  };

  return (
    <div className="App">
      <h1>My Movie Voting and Review App</h1>
      <div>
        <h2>Movie Title</h2>
        <p>Synopsis: Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
        <Stars totalStars={5} initialRating={movieRating} onRate={handleRate} />
        <p>Your rating: {movieRating}</p>
      </div>
    </div>
  );

  return (
       <div className="App">
           <h1>Movie Reviews</h1>
           <MovieList movies={movies} onReviewSubmit={handleReviewSubmit} />
       </div>
   );
}

   

export default App;
